	TEXT="实体 （entities）">
<node ID="Freemind_Link_895967859" 
	TEXT="简单的说：类似于宏定义，用一个名称标识一块数据，引用该名称就引入该数据"/>
<node FOLDED="true" ID="Freemind_Link_1165102836" 
	TEXT="复杂因为有多种分类方法">
<node FOLDED="true" ID="Freemind_Link_1243305325" 
	TEXT="可以用在两种不同文件中">
<node ID="Freemind_Link_280610710" 
	TEXT="DTD 和 文档中"/>
<node ID="Freemind_Link_7794762" 
	TEXT="一般实体和参数实体"/>
</node>
<node ID="Freemind_Link_303185148" 
	TEXT="一般实体和参数实体"/>
<node ID="Freemind_Link_1481148807" 
	TEXT="一般实体又分为内部一般实体和外部一般实体"/>
<node FOLDED="true" ID="Freemind_Link_1669542209" 
	TEXT="两种不同类型的实体">
<node ID="Freemind_Link_1317427579" 
	TEXT="已析和未析"/>
</node>
<node ID="Freemind_Link_958825796" 
	TEXT="两种或三种不同的引用方法"/>
</node>
<node FOLDED="true" ID="Freemind_Link_340098327" 
	TEXT="一般实体和外部实体">
<node FOLDED="true" ID="Freemind_Link_1581017854" 
	TEXT="一般实体">
<node ID="Freemind_Link_515554578" 
	TEXT="一般实体声明位于文档的序言中"/>
<node ID="Freemind_Link_1153838343" 
	TEXT="一般实体的声明可以引用一般实体，除非出现循环调用"/>
<node ID="Freemind_Link_1246837173" 
	TEXT="常用的一般实体的定义可以放在 DTD 中，实现声明一次，多处使用"/>
</node>
<node FOLDED="true" ID="Freemind_Link_76109171" 
	TEXT="外部实体">
<node FOLDED="true" ID="Freemind_Link_1122215977" 
	TEXT="可以在一个文档中引用另外的文档">
<node ID="Freemind_Link_1846665130" 
	TEXT="如: 将一篇文章分为几个部分，用外部实体方式定义、包含"/>
<node ID="Freemind_Link_1010534863" 
	TEXT="如: 引用图片文件"/>
</node>
<node FOLDED="true" ID="Freemind_Link_645220420" 
	TEXT="可析实体">
<node ID="Freemind_Link_691896925" 
	TEXT="外部数据是需要直接包含进来的文本数据，称为 parsed entities"/>
<node FOLDED="true" ID="Freemind_Link_248952373" 
	TEXT="例如">
<node ID="Freemind_Link_1004335877" 
	TEXT="声明：&#xa;    &lt;!ENTITY ch01 SYSTEM &quot;ch01.sgm&quot;&gt;&#xa;    &lt;!ENTITY ch02 SYSTEM &quot;ch02.sgm&quot;&gt;&#xa;    &lt;!ENTITY ch03 SYSTEM &quot;ch03.sgm&quot;&gt;&#xa;    &lt;!ENTITY ch04 SYSTEM &quot;ch04.sgm&quot;&gt;&#xa;&#xa;引用&#xa;    &lt;book&gt;&#xa;    &amp;ch01;&#xa;    &amp;ch02;&#xa;    &amp;ch03;&#xa;    &amp;ch04;&#xa;    &lt;/book&gt;&#xa;"/>
</node>
</node>
<node FOLDED="true" ID="Freemind_Link_153679629" 
	TEXT="非析实体">
<node ID="Freemind_Link_1792725397" 
	TEXT="外部数据是一个图片，则称为 unparsed entities"/>
<node FOLDED="true" ID="Freemind_Link_1491328932" 
	TEXT="例如">
<node ID="Freemind_Link_1057647109" 
	TEXT="声明：&#xa;    &lt;!ENTITY tree SYSTEM &quot;tree.eps&quot; NDATA EPS&gt;&#xa;引用：&#xa;    &lt;graphic entityref=&quot;tree&quot;&gt;&lt;/graphic&gt;&#xa;"/>
</node>
</node>
</node>
</node>
<node FOLDED="true" ID="Freemind_Link_189385219" 
	TEXT="字符实体">
<node FOLDED="true" ID="Freemind_Link_470503677" 
	TEXT="特殊字符">
<node FOLDED="true" ID="Freemind_Link_121180583" 
	TEXT="&lt;, &gt;, &amp; ">
<node ID="Freemind_Link_161625474" 
	TEXT="会被误认为元素或者实体"/>
<node ID="Freemind_Link_1526307972" 
	TEXT="因此需要"/>
</node>
<node ID="Freemind_Link_720014183" 
	TEXT="其它字符，如非打印字符，Unicode 字符，中文等"/>
</node>
<node FOLDED="true" ID="Freemind_Link_780481550" 
	TEXT="SGML ">
<edge WIDTH="thin"/>
<node ID="Freemind_Link_1815700746" 
	TEXT="特殊字符由第三方实体分类文件定义，也称数据实体"/>
</node>
<node FOLDED="true" ID="Freemind_Link_1746898775" 
	TEXT="xml">
<node FOLDED="true" ID="Freemind_Link_608023261" 
	TEXT="XML 可以用形如 &amp;#xhhhh; 的数字字符对照表示 Unicode 字符">
<edge WIDTH="thin"/>
<node ID="Freemind_Link_95064654" 
	TEXT="而 SGML 则必须进行定义"/>
</node>
</node>
</node>
<node FOLDED="true" ID="Freemind_Link_1216258585" 
	TEXT="参数实体">
<node ID="Freemind_Link_1017569783" 
	TEXT="仅出现于 DTD 当中"/>
<node FOLDED="true" ID="Freemind_Link_1555432286" 
	TEXT="SGML 还可以出现于文档当中的标记块中">
<node ID="Freemind_Link_1728280891" 
	TEXT="&lt;![keyword[ and end with ]]&gt;"/>
<node FOLDED="true" ID="Freemind_Link_556040326" 
	TEXT="常见的标记块">
<node ID="Freemind_Link_1192683281" 
	TEXT="INCLUDE">
<arrowlink DESTINATION="Freemind_Link_1555782602" ENDARROW="Default" ENDINCLINATION="160;0;" ID="Freemind_Arrow_Link_1974435129" STARTARROW="None" STARTINCLINATION="160;0;"/>
</node>
<node ID="Freemind_Link_1901817930" 
	TEXT="IGNORE">
<arrowlink DESTINATION="Freemind_Link_277430957" ENDARROW="Default" ENDINCLINATION="285;0;" ID="Freemind_Arrow_Link_346762310" STARTARROW="None" STARTINCLINATION="285;0;"/>
</node>
<node ID="Freemind_Link_58056792" 
	TEXT="CDATA"/>
</node>
</node>
<node FOLDED="true" ID="Freemind_Link_413483893" 
	TEXT="以 % 开始而非 &amp; 符号">
<node FOLDED="true" ID="Freemind_Link_656224884" 
	TEXT="示例">
<node ID="Freemind_Link_1555782602" 
	TEXT="参数实体定义&#xa;    &lt;!ENTITY % draft &quot;IGNORE&quot;&gt;&#xa;引用参数实体，实现对文本块的控制&#xa;    &lt;![%draft;[&#xa;    &lt;para&gt;&#xa;    This paragraph only appears in the draft version.&#xa;    &lt;/para&gt;&#xa;    ]]&gt;"/>
<node ID="Freemind_Link_277430957" 
	TEXT="参数实体定义&#xa;    &lt;!ENTITY % draft &quot;INCLUDE&quot;&gt;&#xa;引用参数实体，实现对文本块的控制&#xa;    &lt;![%draft;[&#xa;    &lt;para&gt;&#xa;    This paragraph only appears in the draft version.&#xa;    &lt;/para&gt;&#xa;    ]]&gt;"/>
</node>
</node>
</node>
</node>
