</node>
<node FOLDED="true" ID="Freemind_Link_753717281" 
	TEXT="SGML 的特征">
<node ID="Freemind_Link_1204124054" LINK="http://xml.coverpages.org/general.html#overview" 
	TEXT="SGML 资料，参见： http://xml.coverpages.org/general.html#overview"/>
<node FOLDED="true" ID="Freemind_Link_1861830281" 
	TEXT="文档结构">
<node FOLDED="true" ID="Freemind_Link_396512422" 
	TEXT="序言">
<node ID="Freemind_Link_553132863" 
	TEXT="SGML 文档必须有一个序言"/>
<node FOLDED="true" ID="Freemind_Link_24793231" 
	TEXT="SGML Declaration (可选)">
<node ID="Freemind_Link_269104213" 
	TEXT="一个可选的 SGML Declaration（SGML声明）"/>
<node ID="Freemind_Link_742624143" 
	TEXT="但一般和 DTD 一起存放在外部的文件中，命名为“docbook.dcl”；"/>
<node ID="Freemind_Link_74776887" 
	TEXT="在 catalog 文件中：&#xa;    SGMLDECL &quot;dtd/4.2/docbook.dcl&quot;"/>
<node ID="Freemind_Link_1861041837" LINK="http://www.oasis-open.org/cover/wlw11.html" 
	TEXT="SGML Declaration 参考： http://www.oasis-open.org/cover/wlw11.html"/>
</node>
<node FOLDED="true" ID="Freemind_Link_365287773" 
	TEXT="Document Type Declaration (必选)">
<node FOLDED="true" ID="Freemind_Link_1605194585" 
	TEXT="例如: ">
<node ID="Freemind_Link_757060037" 
	TEXT="&lt;!DOCTYPE book PUBLIC &quot;-//OASIS//DTD DocBook V3.1//EN&quot;&gt;"/>
<node ID="Freemind_Link_1565620194" 
	TEXT="&lt;!DOCTYPE book PUBLIC &quot;-//OASIS//DTD DocBook V3.1//EN&quot; [&#xa;&lt;!ENTITY nwalsh &quot;Norman Walsh&quot;&gt;&#xa;&lt;!ENTITY chap1 SYSTEM &quot;chap1.sgm&quot;&gt;&#xa;&lt;!ENTITY chap2 SYSTEM &quot;chap2.sgm&quot;&gt;&#xa;]&gt;"/>
</node>
<node FOLDED="true" ID="Freemind_Link_1658577955" 
	TEXT="声明根元素是什么">
<node ID="Freemind_Link_1199246756" 
	TEXT="book"/>
</node>
<node FOLDED="true" ID="Freemind_Link_1809585408" 
	TEXT="声明使用的 DTD">
<node FOLDED="true" ID="Freemind_Link_657034349" 
	TEXT="内部子集">
<node ID="Freemind_Link_1646518634" 
	TEXT="&lt;!ENTITY nwalsh &quot;Norman Walsh&quot;&gt;&#xa;&lt;!ENTITY chap1 SYSTEM &quot;chap1.sgm&quot;&gt;&#xa;&lt;!ENTITY chap2 SYSTEM &quot;chap2.sgm&quot;&gt;"/>
</node>
<node FOLDED="true" ID="Freemind_Link_1864779170" 
	TEXT="外部子集">
<node ID="Freemind_Link_241620489" 
	TEXT="例如: PUBLIC &quot;-//OASIS//DTD DocBook V3.1//EN&quot;"/>
<node FOLDED="true" ID="Freemind_Link_495766221" 
	TEXT="如何引用外部子集？&#xa;public/system 标志符&#xa;catalog 文件">
<node ID="Freemind_Link_1912814272" 
	TEXT="可以用 Public 或者 SYSTEM 标志符"/>
<node FOLDED="true" ID="Freemind_Link_1905545691" 
	TEXT="SGML 多用 PUBLIC 标志符，因为有更好的移植性">
<node ID="Freemind_Link_1350096635" 
	TEXT="XML 降低了 Public 标志符地位。&#xa;XML 必须有 System 标志符，而不必有 Public 标志符"/>
</node>
<node FOLDED="true" ID="Freemind_Link_709043337" 
	TEXT="实际上 Public 标志符依赖 Catalog 文件">
<arrowlink DESTINATION="Freemind_Link_232845324" ENDARROW="Default" ENDINCLINATION="937;0;" ID="Freemind_Arrow_Link_838826432" STARTARROW="None" STARTINCLINATION="937;0;"/>
<node ID="Freemind_Link_1359984627" 
	TEXT="使用外部的 Catalog 文件，建立 Public 标志符到本地的文件映射"/>
</node>
<node FOLDED="true" ID="Freemind_Link_1798085904" 
	TEXT="SYSTEM 标志符格式不限&#xa;">
<node ID="Freemind_Link_530035251" 
	TEXT="在 SGML 中通常是直接指向本地的文件名， 因在移植上的问题，一般多用 Public 标志符"/>
<node ID="Freemind_Link_1903009334" 
	TEXT="如： /usr/local/sgml/docbook/3.1/docbook.dtd "/>
</node>
</node>
</node>
<node ID="Freemind_Link_1799556276" 
	TEXT="内部子集的声明优先于外部子集"/>
</node>
</node>
</node>
<node FOLDED="true" ID="Freemind_Link_918881546" 
	TEXT="根元素">
<node ID="Freemind_Link_843340671" 
	TEXT="根元素紧接着序言部分（当然注释、处理指令（processing instructions）除外）"/>
</node>
</node>
<node FOLDED="true" ID="Freemind_Link_598657270" 
	TEXT="与 XML 兼容性问题">
<node FOLDED="true" ID="Freemind_Link_1453966077" 
	TEXT="大小写问题">
<node ID="Freemind_Link_265225593" 
	TEXT="SGML中 元素、属性大小写不敏感"/>
<node ID="Freemind_Link_300268117" 
	TEXT="但是，实体名称大小写敏感"/>
<node ID="Freemind_Link_1506504046" 
	TEXT="为向 XML 移植方便，请都用小写字母"/>
</node>
<node FOLDED="true" ID="Freemind_Link_1094046607" 
	TEXT="属性是否用引号的问题">
<node ID="Freemind_Link_1827166786" 
	TEXT="除非属性值包含空格，否则可以不用引号将属性值括起来；"/>
<node ID="Freemind_Link_211931488" 
	TEXT="XML必须用引号将属性值扩起来"/>
</node>
</node>
<node FOLDED="true" ID="Freemind_Link_1269856182" 
	TEXT="如何将文章分解成几块&#xa;（利用外部实体）">
<node ID="Freemind_Link_528907337" 
	TEXT="&lt;!DOCTYPE book PUBLIC &quot;-//OASIS//DTD DocBook V3.1//EN&quot; [&#xa;&lt;!ENTITY chap1 SYSTEM &quot;chap1.sgm&quot;&gt;&#xa;&lt;!ENTITY chap2 SYSTEM &quot;chap2.sgm&quot;&gt;&#xa;&lt;!ENTITY chap3 SYSTEM &quot;chap3.sgm&quot;&gt;&#xa;&lt;!ENTITY appa SYSTEM &quot;appa.sgm&quot;&gt;&#xa;&lt;!ENTITY appb SYSTEM &quot;appb.sgm&quot;&gt;&#xa;]&gt;&#xa;&lt;book&gt;&lt;title&gt;My First Book&lt;/title&gt;&#xa;&amp;chap1;&#xa;&amp;chap2;&#xa;&amp;chap3;&#xa;&amp;appa;&#xa;&amp;appb;&#xa;&lt;/book&gt;"/>
<node FOLDED="true" ID="Freemind_Link_1991033682" 
	TEXT="&lt;chapter id=&quot;ch1&quot;&gt;&lt;title&gt;My First Chapter&lt;/title&gt;&#xa;&lt;para&gt;My first paragraph.&lt;/para&gt;&#xa;...">
<node ID="Freemind_Link_1864444288" 
	TEXT="注意不要在该文档文件头加入 DTD!"/>
</node>
</node>
</node>
<node FOLDED="true" ID="Freemind_Link_605612777" 
	TEXT="XML 的特征">
<node ID="Freemind_Link_1659647593" LINK="http://xml.coverpages.org/xmlIntro.html" 
