	TEXT="&lt;xsl:template name=&quot;section.heading&quot;&gt;&#xa;  &lt;xsl:param name=&quot;level&quot; select=&quot;1&quot;/&gt;&#xa;  &lt;xsl:param name=&quot;title&quot;/&gt;&#xa;&#xa;  &lt;fo:block  xsl:use-attribute-sets=&quot;section.title.properties&quot;&gt;&#xa;  ...&#xa;&#xa;  &lt;/fo:block&gt;&#xa;&lt;/xsl:template&gt;"/>
