	TEXT="catalogs=catalog.xml;../../dtd/docbook44/docbook.cat&#xa;relative-catalogs=false&#xa;static-catalog=yes&#xa;catalog-class-name=org.apache.xml.resolver.Resolver&#xa;verbosity=1&#xa;"/>
