	TEXT="设置 CLASSPATH">
<node ID="Freemind_Link_1169946472" 
	TEXT="CLASSPATH=$CLASSPATH:/usr/Xalan/xalan.jar:/usr/Xalan/xml-apis.jar:\&#xa;/usr/Xalan/xercesImpl.jar:/usr/docbook-xsl/extensions/xalan25.jar&#xa;export CLASSPATH&#xa;"/>
