	TEXT="xsltproc  \&#xa;    --output myfile.fo  &#xa;    --stringparam fop.extensions 1  \&#xa;    docbook-xsl/fo/docbook.xsl  \&#xa;    myfile.xml"/>
</node>
</node>
<node FOLDED="true" ID="Freemind_Link_230867681" 
	TEXT="Convert a .fo file on Unix or Linux:&#xa;">
<node ID="Freemind_Link_1522662986" 
	TEXT="fop.sh -fo myfile.fo -pdf myfile.pdf&#xa;&#xa;"/>
</node>
<node FOLDED="true" ID="Freemind_Link_1710334298" 
	TEXT="Convert an XML source file Unix or Linux:&#xa;">
<node ID="Freemind_Link_1189182883" 
	TEXT="fop.sh -xsl /docbook-xsl/fo/docbook.xsl -xml myfile.xml -pdf myfile.pdf&#xa;&#xa;"/>
</node>
<node FOLDED="true" ID="Freemind_Link_1470600907" 
	TEXT="Convert a .fo file on Windows:&#xa;">
<node ID="Freemind_Link_882378889" 
	TEXT="fop.bat -fo myfile.fo -pdf myfile.pdf&#xa;&#xa;"/>
</node>
<node FOLDED="true" ID="Freemind_Link_769799273" 
	TEXT="Convert an XML source file on Windows:&#xa;">
<node ID="Freemind_Link_1107432910" 
	TEXT="fop.bat -xsl /docbook-xsl/fo/docbook.xsl -xml myfile.xml -pdf myfile.pdf&#xa;"/>
