	TEXT="Setting CLASSPATH:">
<node ID="Freemind_Link_1181304357" 
	TEXT="CLASSPATH=&quot;/usr/java/fop-0.20.5/build/fop.jar:\&#xa;/usr/java/fop-0.20.5/lib/batik.jar:\&#xa;/usr/java/fop-0.20.5/xalan-version.jar:\&#xa;/usr/java/fop-0.20.5/lib/xercesImpl-version.jar:\&#xa;/usr/java/fop-0.20.5/lib/JimiProClasses.jar\&#xa;/usr/java/avalon-framework-cvs-version.jar&quot; &#xa;export CLASSPATH"/>
</node>
<node FOLDED="true" ID="Freemind_Link_473387290" 
	TEXT="General syntax:&#xa;">
<node ID="Freemind_Link_711918104" 
	TEXT="java  org.apache.fop.apps.Fop  [options]  \&#xa;    [-fo|-xml] infile  \&#xa;    [-xsl stylesheet-path]   \&#xa;    -pdf  outfile.pdf&#xa;&#xa;"/>
