<node ID="Freemind_Link_1355734229" 
	TEXT="&lt;para&gt;&#xa;The equation e&lt;superscript&gt;&amp;pi;i&lt;/superscript&gt; + 1 = 0 ties together&#xa;five of the most important mathematical constants.&#xa;&lt;/para&gt;"/>
</node>
</node>
<node FOLDED="true" ID="Freemind_Link_479355822" 
	TEXT="subscript">
<node ID="Freemind_Link_1424067763" 
	TEXT="下标"/>
<node FOLDED="true" ID="Freemind_Link_1834753255" 
	TEXT="例如">
<node ID="Freemind_Link_258965737" 
	TEXT="&lt;para&gt;&#xa;Thirsty?  Have some H&lt;subscript&gt;2&lt;/subscript&gt;O.&#xa;&lt;/para&gt;"/>
</node>
</node>
<node FOLDED="true" ID="Freemind_Link_883541509" 
	TEXT="quote">
<node ID="Freemind_Link_1483191689" 
	TEXT="引用文字，即将文字两端添加引号（或其它）扩起来。"/>
